public void testBadSetup()
{
  try {
    // setup the server...
    String url = "http://localhost/?core=xxx";
    CommonsHttpSolrServer s = new CommonsHttpSolrServer( url );
    Assert.fail( "CommonsHttpSolrServer should not allow a path with a parameter: "+s.getBaseURL() );
  }
  catch( Exception ex ) {
    // expected
  }
}

