/** Write byte into byte slice stream */
public void writeByte(byte b) {
  assert slice != null;
  if (slice[upto] != 0) {
    upto = pool.allocSlice(slice, upto);
    slice = pool.buffer;
    offset0 = pool.byteOffset;
    assert slice != null;
  }
  slice[upto++] = b;
  assert upto != slice.length;
}

