private static final int compareChars(char[] chars1, int len1,
                                      char[] chars2, int len2) {
  final int end = len1 < len2 ? len1:len2;
  for (int k = 0; k < end; k++) {
    char c1 = chars1[k];
    char c2 = chars2[k];
    if (c1 != c2) {
      return c1 - c2;
    }
  }
  return len1 - len2;
}

