/*
 * Make a class field accessible
 */
private static void makeAccessible(final Field field) {
  if (System.getSecurityManager() == null) {
    field.setAccessible(true);
  } else {
    AccessController.doPrivileged(new PrivilegedAction<Void>() {
      public Void run() {
        field.setAccessible(true);
        return null;
      }
    });
  }
}

