protected BaseUIMATokenizer(Reader reader, String descriptorPath) {
  super(reader);
  try {
    ae = AEProviderFactory.getInstance().getAEProvider("", descriptorPath).getAE();
    cas = ae.newCAS();
  } catch (ResourceInitializationException e) {
    throw new RuntimeException(e);
  }
}

