public IndexableField getField(FieldInfo fieldInfo) {  
  Integer num = fields.get(fieldInfo.number);
  if (num == null) {
    num = 0;
  } else {
    num++;
  }
  fields.put(fieldInfo.number, num);

  return new LazyField(fieldInfo.name, num);
}

