    public Collection<byte[]> getPayload() throws IOException {
      ArrayList<byte[]> result = null;
      if (includeSpans.isPayloadAvailable()) {
        result = new ArrayList<byte[]>(includeSpans.getPayload());
      }
      return result;
    }

