  IndexWriter writer = new IndexWriter(dir, new WhitespaceAnalyzer(), true);
  writer.setMaxBufferedDocs(10);
  writer.setMergeFactor(100);

  for (int i = 0; i < 250; i++) {
    addDoc(writer);
    checkInvariants(writer);
  }
  writer.close();

  IndexReader reader = IndexReader.open(dir);
