  writer = new IndexWriter(dir, new WhitespaceAnalyzer(), false);
  writer.setMaxBufferedDocs(10);
  writer.setMergeFactor(5);

  for (int i = 0; i < 10; i++) {
    addDoc(writer);
    checkInvariants(writer);
  }
  checkInvariants(writer);
  assertEquals(10, writer.docCount());

  writer.close();
}

