@Override
@After
public void tearDown() throws Exception {
  if (VERBOSE) {
    super.printLayout();
  }
  ((CommonsHttpSolrServer) controlClient).shutdown();
  if (cloudClient != null) {
    cloudClient.close();
  }
  if (zkStateReader != null) {
    zkStateReader.close();
  }
  super.tearDown();

  System.clearProperty("zkHost");
}

