public Document doc(int i) throws IOException {
  Document d;
  if (documentCache != null) {
    d = (Document)documentCache.get(i);
    if (d!=null) return d;
  }

  d = searcher.doc(i);

  if (documentCache != null) {
    documentCache.put(i,d);
  }

  return d;
}

/**
