  ArrayList<Closeable> perfObjectsToClose = new ArrayList<Closeable>();
  for (Object obj : perfObjects.values()) {
    if (obj instanceof Closeable) {
      perfObjectsToClose.add((Closeable) obj);
    }
  }
  IOUtils.close(perfObjectsToClose);
}

