void addText(String text) throws IOException {
  if (inScript)
    return;
  if (inStyle)
    return;
  if (inMetaTag)
  {
      metaTags.setProperty(currentMetaTag, text);
      return;
  }
  if (inTitle)
    title.append(text);
  else {
    addToSummary(text);
    if (!titleComplete && !title.equals("")) {  // finished title
      synchronized(this) {
        titleComplete = true;                   // tell waiting threads
        notifyAll();
      }
    }
  }

  length += text.length();
  pipeOut.write(text);

  afterSpace = false;
}

