/**
 * Increment the generation count for the norms file for
 * this field.
 *
 * @param fieldIndex field whose norm file will be rewritten
 */
void advanceNormGen(int fieldIndex) {
  if (normGen[fieldIndex] == -1) {
    normGen[fieldIndex] = 1;
  } else {
    normGen[fieldIndex]++;
  }
}

