private Field.Index getIndexType(FieldInfo fi, boolean tokenize) {
  Field.Index index;
  if (fi.isIndexed && tokenize)
    index = Field.Index.TOKENIZED;
  else if (fi.isIndexed && !tokenize)
    index = Field.Index.UN_TOKENIZED;
  else
    index = Field.Index.NO;
  return index;
}

private Field.Index getIndexType(FieldInfo fi, boolean tokenize) {
