private static String fields(Set<String> fieldSet) {
  StringBuilder buf = new StringBuilder();
  boolean comma = false;
  for(String field : fieldSet) {
    if(comma) {
      buf.append(",");
    }
    buf.append(field);
    comma = true;
  }

  return buf.toString();
}

