      assertRefCountEquals(2, subReaders0[i]);
      if (subReaders0[i] != subReaders1[i]) {
        assertRefCountEquals(1, subReaders1[i]);
      }
    }

    // delete first document, so that only one of the subReaders have to be re-opened
