/**
 * @exception org.apache.lucene.queryparser.classic.ParseException throw in overridden method to disallow
 */
protected Query newFieldQuery(Analyzer analyzer, String field, String queryText, boolean quoted)  throws ParseException {
  // Use the analyzer to get all the tokens, and then build a TermQuery,
  // PhraseQuery, or nothing based on the term count
  CachingTokenFilter buffer = null;
  TermToBytesRefAttribute termAtt = null;
  PositionIncrementAttribute posIncrAtt = null;
  int numTokens = 0;
  int positionCount = 0;
  boolean severalTokensAtSamePosition = false;
  boolean hasMoreTokens = false;    

  try (TokenStream source = analyzer.tokenStream(field, queryText)) {
    source.reset();
    buffer = new CachingTokenFilter(source);
    buffer.reset();

    if (buffer.hasAttribute(TermToBytesRefAttribute.class)) {
      termAtt = buffer.getAttribute(TermToBytesRefAttribute.class);
    }
    if (buffer.hasAttribute(PositionIncrementAttribute.class)) {
      posIncrAtt = buffer.getAttribute(PositionIncrementAttribute.class);
    }

    if (termAtt != null) {
      try {
        hasMoreTokens = buffer.incrementToken();
        while (hasMoreTokens) {
          numTokens++;
          int positionIncrement = (posIncrAtt != null) ? posIncrAtt.getPositionIncrement() : 1;
          if (positionIncrement != 0) {
            positionCount += positionIncrement;
          } else {
            severalTokensAtSamePosition = true;
          }
          hasMoreTokens = buffer.incrementToken();
        }
      } catch (IOException e) {
        // ignore
      }
    }
  } catch (IOException e) {
    ParseException p = new ParseException("Eror analyzing query text");
    p.initCause(e);
    throw p;
  }

  // rewind the buffer stream
  buffer.reset();

  BytesRef bytes = termAtt == null ? null : termAtt.getBytesRef();

  if (numTokens == 0)
    return null;
  else if (numTokens == 1) {
    try {
      boolean hasNext = buffer.incrementToken();
      assert hasNext == true;
      termAtt.fillBytesRef();
    } catch (IOException e) {
      // safe to ignore, because we know the number of tokens
    }
    return newTermQuery(new Term(field, BytesRef.deepCopyOf(bytes)));
  } else {
    if (severalTokensAtSamePosition || (!quoted && !autoGeneratePhraseQueries)) {
      if (positionCount == 1 || (!quoted && !autoGeneratePhraseQueries)) {
        // no phrase query:

        if (positionCount == 1) {
          // simple case: only one position, with synonyms
          BooleanQuery q = newBooleanQuery(true);
          for (int i = 0; i < numTokens; i++) {
            try {
              boolean hasNext = buffer.incrementToken();
              assert hasNext == true;
              termAtt.fillBytesRef();
            } catch (IOException e) {
              // safe to ignore, because we know the number of tokens
            }
            Query currentQuery = newTermQuery(
                new Term(field, BytesRef.deepCopyOf(bytes)));
            q.add(currentQuery, BooleanClause.Occur.SHOULD);
          }
          return q;
        } else {
          // multiple positions
          BooleanQuery q = newBooleanQuery(false);
          final BooleanClause.Occur occur = operator == Operator.AND ? BooleanClause.Occur.MUST : BooleanClause.Occur.SHOULD;
          Query currentQuery = null;
          for (int i = 0; i < numTokens; i++) {
            try {
              boolean hasNext = buffer.incrementToken();
              assert hasNext == true;
              termAtt.fillBytesRef();
            } catch (IOException e) {
              // safe to ignore, because we know the number of tokens
            }
            if (posIncrAtt != null && posIncrAtt.getPositionIncrement() == 0) {
              if (!(currentQuery instanceof BooleanQuery)) {
                Query t = currentQuery;
                currentQuery = newBooleanQuery(true);
                ((BooleanQuery)currentQuery).add(t, BooleanClause.Occur.SHOULD);
              }
              ((BooleanQuery)currentQuery).add(newTermQuery(new Term(field, BytesRef.deepCopyOf(bytes))), BooleanClause.Occur.SHOULD);
            } else {
              if (currentQuery != null) {
                q.add(currentQuery, occur);
              }
              currentQuery = newTermQuery(new Term(field, BytesRef.deepCopyOf(bytes)));
            }
          }
          q.add(currentQuery, occur);
          return q;
        }
      }
      else {
        // phrase query:
        MultiPhraseQuery mpq = newMultiPhraseQuery();
        mpq.setSlop(phraseSlop);
        List<Term> multiTerms = new ArrayList<Term>();
        int position = -1;
        for (int i = 0; i < numTokens; i++) {
          int positionIncrement = 1;
          try {
            boolean hasNext = buffer.incrementToken();
            assert hasNext == true;
            termAtt.fillBytesRef();
            if (posIncrAtt != null) {
              positionIncrement = posIncrAtt.getPositionIncrement();
            }
          } catch (IOException e) {
            // safe to ignore, because we know the number of tokens
          }

          if (positionIncrement > 0 && multiTerms.size() > 0) {
            if (enablePositionIncrements) {
              mpq.add(multiTerms.toArray(new Term[0]),position);
            } else {
              mpq.add(multiTerms.toArray(new Term[0]));
            }
            multiTerms.clear();
          }
          position += positionIncrement;
          multiTerms.add(new Term(field, BytesRef.deepCopyOf(bytes)));
        }
        if (enablePositionIncrements) {
          mpq.add(multiTerms.toArray(new Term[0]),position);
        } else {
          mpq.add(multiTerms.toArray(new Term[0]));
        }
        return mpq;
      }
    }
    else {
      PhraseQuery pq = newPhraseQuery();
      pq.setSlop(phraseSlop);
      int position = -1;

      for (int i = 0; i < numTokens; i++) {
        int positionIncrement = 1;

        try {
          boolean hasNext = buffer.incrementToken();
          assert hasNext == true;
          termAtt.fillBytesRef();
          if (posIncrAtt != null) {
            positionIncrement = posIncrAtt.getPositionIncrement();
          }
        } catch (IOException e) {
          // safe to ignore, because we know the number of tokens
        }

        if (enablePositionIncrements) {
          position += positionIncrement;
          pq.add(new Term(field, BytesRef.deepCopyOf(bytes)),position);
        } else {
          pq.add(new Term(field, BytesRef.deepCopyOf(bytes)));
        }
      }
      return pq;
    }
  }
}
