public GlobFileFilter getFileFilterFromFileTypes(String fileTypes) {
  String glob;
  if(fileTypes.equals("*"))
    glob = ".*";
  else
    glob = "^.*\\.(" + fileTypes.replace(",", "|") + ")$";
  return new GlobFileFilter(glob, true);
}

