public void close() {
  isClosed = true;
  if(tpe != null) {
    if (!tpe.isShutdown()) {
      tpe.shutdown();
      try {
        tpe.awaitTermination(60, TimeUnit.SECONDS);
      } catch (InterruptedException e) {
        log.warn("Thread interrupted while waiting for OCP threadpool close.");
        Thread.currentThread().interrupt();
      } finally {
        if (!tpe.isShutdown())
          tpe.shutdownNow();
      }
    }
  }
}

