private void notifyReaderClosedListeners(Throwable th) {
  synchronized(readerClosedListeners) {
    for(ReaderClosedListener listener : readerClosedListeners) {
      try {
        listener.onClose(this);
      } catch (Throwable t) {
        if (th == null) {
          th = t;
        } else {
          th.addSuppressed(t);
        }
      }
    }
    IOUtils.reThrowUnchecked(th);
  }
}

