@Override
public boolean incrementToken() throws IOException {
  if (input.incrementToken()) {
    if (!keywordAttr.isKeyword()) {
      final int newlen = stemmer.stem(termAtt.buffer(), termAtt.length());
      termAtt.setLength(newlen);
    }
    return true;
  } else {
    return false;
  }
}

