@Override
public int readVInt() throws IOException {
  if (5 <= (bufferLength-bufferPosition)) {
    byte b = buffer[bufferPosition++];
    int i = b & 0x7F;
    for (int shift = 7; (b & 0x80) != 0; shift += 7) {
      b = buffer[bufferPosition++];
      i |= (b & 0x7F) << shift;
    }
    return i;
  } else {
    return super.readVInt();
  }
}

