/**
 * Convenience method for building up SolrInputDocuments
 */
final SolrInputDocument doc(SolrInputField... fields) {
  SolrInputDocument d = new SolrInputDocument();
  for (SolrInputField f : fields) {
    d.put(f.getName(), f);
  }
  return d;
}

