Path maybeChroot(Path path) {
  if (path.toAbsolutePath().startsWith("/sys") || path.toAbsolutePath().startsWith("/dev")) {
    // map to our chrooted location;
    return path.getRoot().resolve(root).resolve(path.toString().substring(1));
  } else {
    return path;
  }
}

