protected SpanScorer(Spans spans, Weight weight, Similarity.SloppySimScorer docScorer)
throws IOException {
  super(weight);
  this.docScorer = docScorer;
  this.spans = spans;

  if (this.spans.next()) {
    doc = -1;
  } else {
    doc = NO_MORE_DOCS;
    more = false;
  }
}

