@Override
public void add(int docID, long v) throws IOException {
  assert lastDocId < docID;
  if (!started) {
    started = true;
    minValue = maxValue = v;
  } else {
    if (v < minValue) {
      minValue = v;
    } else if (v > maxValue) {
      maxValue = v;
    }
  }
  lastDocId = docID;
  BytesRefUtils.copyLong(bytesRef, v);
  add(docID, bytesRef);
}

