private String mergeAll(String[] parts, int i) {
  if (i == parts.length - 1)
    return parts[parts.length - 1];
  StringBuffer sb = new StringBuffer();
  for (int j = i; j < parts.length; j++) {
    sb.append(parts[j]);
    if (j < parts.length - 1)
      sb.append(".");
  }
  return sb.toString();
}

