protected void set(BytesRef ref, int docId) {
  assert BYTE_BLOCK_SIZE % size == 0 : "BYTE_BLOCK_SIZE ("+ BYTE_BLOCK_SIZE + ") must be a multiple of the size: " + size;
  ref.offset = docId*size;
  ref.length = size;
  pool.deref(ref);
}

