public PluginBag(Class<T> klass, SolrCore core) {
  this.core = core;
  this.klass = klass;
  meta = SolrConfig.classVsSolrPluginInfo.get(klass.getName());
  if (meta == null) {
    throw new SolrException(SolrException.ErrorCode.SERVER_ERROR, "Unknown Plugin : " + klass.getName());
  }
}

