public void remove( String name ) 
{
  cores.remove( name );
}

// TODO? -- add some kind of hook to close the core after all references are 
// gone...  is finalize() enough?
public void remove( String name ) {
  synchronized(cores) {
    CoreDescriptor dcore = cores.remove( name );
    if (dcore == null) {
      return;
    }

    SolrCore core = dcore.getCore();
    if (core != null) {
      core.close();
    }
  }
}

