/** 
 * Test sort with a danish collator. ö is ordered after z
 */
public void testBasicSort() {
  assertQ("Collated Sort: ",
      req("fl", "id", "q", "sort_da:[tz TO töz]", "sort", "sort_da asc" ),
             "//*[@numFound='2']",
             "//result/doc[1]/int[@name='id'][.=11]",
             "//result/doc[2]/int[@name='id'][.=4]"
   );
}

