public char getChar(Map<String,String> args, String name, char defaultValue) {
  String s = args.remove(name);
  if (s == null) {
    return defaultValue;
  } else { 
    if (s.length() != 1) {
      throw new IllegalArgumentException(name + " should be a char. \"" + s + "\" is invalid");
    } else {
      return s.charAt(0);
    }
  }
}

