public static String getAsString(String field, SolrDocument doc) {
  Object v = doc.getFirstValue(field);
  if(v != null) {
    if(v instanceof StoredField) {
      return ((StoredField)v).stringValue();
    }
    return v.toString();
  }
  return null;
}

