@Override
public BytesRef getBytes(int docID) {
  final int address = (int) addresses.get(docID);
  bytesRef.offset = address;
  if (docID == maxDoc - 1) {
    bytesRef.length = totBytes - bytesRef.offset;
  } else {
    bytesRef.length = (int) addresses.get(1 + docID) - bytesRef.offset;
  }
  return bytesRef;
}

