@Override
public void delete() {
  synchronized(SnapshotDeletionPolicy.this) {
    // Suppress the delete request if this commit point is
    // our current snapshot.
    if (snapshot == null || !snapshot.equals(getSegmentsFileName()))
      cp.delete();
  }
}

