  IndexWriter w = new IndexWriter(dir,
                                  new IndexWriterConfig(new MockAnalyzer(random())));

  w.prepareCommit();
  w.rollback();
  assertFalse(DirectoryReader.indexExists(dir));
  dir.close();
}

