/** 
 * @see java.lang.Runnable#run() 
 */ 
public void run() { 

    while (!IDGenerator.this.stopped.get()) { 
        try { 
            this.queue.put(produce()); 
        } catch (InterruptedException e) {
            LOGGER 
                    .warn("UIDProducer has been interrupted -- runner is going down"); 
            return; 
        } 
    } 

}

