public void testMessageLoading_ja() {
  Message invalidSyntax = new MessageImpl(
      MessagesTestBundle.Q0001E_INVALID_SYNTAX, new Object[]{"XXX"});
  assertEquals("構文エラー: XXX", invalidSyntax
      .getLocalizedMessage(Locale.JAPANESE));
}

