public void testThreadInterruptDeadlock() throws Exception {
  IndexerThreadInterrupt t = new IndexerThreadInterrupt();
  t.setDaemon(true);
  t.start();

  // issue 100 interrupts to child thread
  int i = 0;
  while(i < 100) {
    Thread.sleep(1);

    if (t.allowInterrupt) {
      i++;
      t.allowInterrupt = false;
      t.interrupt();
    }
    if (!t.isAlive()) {
      break;
    }
  }
  t.allowInterrupt = false;
  t.finish = true;
  t.interrupt();
  t.join();
  assertFalse(t.failed);
}

