  ExecutorUtil.shutdownAndAwaitTermination(notifications);
  ExecutorUtil.shutdownAndAwaitTermination(collectionPropsNotifications);
  if (closeClient) {
    zkClient.close();
  }
  assert ObjectReleaseTracker.release(this);
}

