int forcePurge(IndexWriter writer) throws IOException {
  assert !Thread.holdsLock(this);
  assert !Thread.holdsLock(writer);
  purgeLock.lock();
  try {
    return innerPurge(writer);
  } finally {
    purgeLock.unlock();
  }
}

