public void testMaxTermLength() throws Exception {
  StandardAnalyzer sa = new StandardAnalyzer();
  sa.setMaxTokenLength(5);
  assertAnalyzesTo(sa, "ab cd toolong xy z", new String[]{"ab", "cd", "xy", "z"});
}

