/**
 * Expert: set the merge policy used by this writer.
 */
public void setMergePolicy(MergePolicy mp) {
  ensureOpen();
  if (mp == null)
    throw new NullPointerException("MergePolicy must be non-null");

  if (mergePolicy != mp)
    mergePolicy.close();
  mergePolicy = mp;
  pushMaxBufferedDocs();
}

