      if(luceneMatchVersion.onOrAfter(Version.LUCENE_4_0_0_ALPHA)) {
        throw new SolrException(ErrorCode.FORBIDDEN, "<indexDefaults> and <mainIndex> configuration sections are discontinued. Use <indexConfig> instead.");
