/**
 * Mark whether this segment is stored as a compound file.
 *
 * @param isCompoundFile true if this is a compound file;
 * else, false
 */
void setUseCompoundFile(boolean isCompoundFile) {
  if (isCompoundFile) {
    this.isCompoundFile = 1;
  } else {
    this.isCompoundFile = -1;
  }
}

