private void reset() {
  pending.clear();
  pending.trimToSize();
  hash.clear();
  iwBytesUsed.addAndGet(-bytesUsed);
  bytesUsed = 0;
  fixedLength = -2;
  maxLength = 0;
}

private void reset() {
  iwBytesUsed.addAndGet((pending.length - DEFAULT_PENDING_SIZE) * RamUsageEstimator.NUM_BYTES_INT);
  pending = ArrayUtil.shrink(pending, DEFAULT_PENDING_SIZE);
  pendingIndex = 0;
  hash.clear();
  fixedLength = -2;
  maxLength = 0;
}

