@Override
public void score(Collector collector) throws IOException {
  if (random.nextBoolean()) {
    try {
      final boolean remaining = in.score(collector, DocsEnum.NO_MORE_DOCS);
      assert !remaining;
    } catch (UnsupportedOperationException e) {
      in.score(collector);
    }
  } else {
    in.score(collector);
  }
}

