int getDelCount() throws IOException {
  if (delCount == -1) {
    if (hasDeletions()) {
      final String delFileName = getDelFileName();
      delCount = new BitVector(dir, delFileName).count();
    } else
      delCount = 0;
  }
  assert delCount <= docCount;
  return delCount;
}

