	Document d = new Document();
	Field f = new Field(FIELD_NAME, "multiOne", true, true, true);
	d.add(f);		
	writer1.addDocument(d);
	writer1.optimize();
	writer1.close();
	IndexReader reader1 = IndexReader.open(ramDir1);

	//setup index 2
	RAMDirectory ramDir2 = new RAMDirectory();
